mapper.select:
SELECT NODE_ID, NODE_TYPE, TRAFFIC_LIGHT, NODE_NAME, DISTRICT_ID, DISTRICT_ID2, WKT, USE_YN FROM NETEDIT_MS_NODE_2020 WHERE NODE_ID IN ( SELECT UP_FROM_NODE FROM NETEDIT_MS_LINK_2020 WHERE 1=1
AND FIRST_GU IN
#{item}
AND ST_Intersects( ST_GeometryFromText(#{WKT}), GEOM )
UNION ALL
SELECT UP_TO_NODE FROM NETEDIT_MS_LINK_2020 WHERE 1=1
AND FIRST_GU IN
#{item}
AND ST_Intersects( ST_GeometryFromText(#{WKT}), GEOM )
)

SELECT 'FROM' AS DATA_TYPE, NODE_ID, NODE_TYPE, TRAFFIC_LIGHT, NODE_NAME, DISTRICT_ID, DISTRICT_ID2, WKT, USE_YN FROM NETEDIT_MS_NODE_2020 WHERE NODE_ID = #{FROM_NODE}
UNION ALL
SELECT 'TO' AS DATA_TYPE, NODE_ID, NODE_TYPE, TRAFFIC_LIGHT, NODE_NAME, DISTRICT_ID, DISTRICT_ID2, WKT, USE_YN FROM NETEDIT_MS_NODE_2020 WHERE NODE_ID = #{TO_NODE}
